import React, { Component } from 'react'
import data from "./data.json"

class ProformaItem extends Component {
    render() {
        return(
            <table border="1">
                <thead>
                    <tr>
                {
                    data.location.map((data, j) => {
                        return(
                                
                                    
                                        <th key={j}>{data.name}</th>
                                    
                                
                        )
                    })
                }
                        <th>Description</th>
                        <th>Product</th>
                        <th>Total Stock</th>
                    </tr>
                </thead>
                <tbody>
                    
                {
                    data.proformaItem.map((data, i) => {
                        return (
                            <tr>
                                {
                                    data.location.map((loc, x) => {
                                        <td key={x}>{loc.id}</td>
                                    })
                                }
                                <td key={i}>
                                    {data.categoryDescription}
                                </td>
                                <td key={i}>
                                    {data.productDescription}
                                </td>
                            </tr>
                                    
                        )
                    })
                }
                    
                </tbody>
            </table>
        )
    }
}
export default ProformaItem
